import React from "react";
import Link from "next/link";
import { useForm } from "react-hook-form";
import styled from "styled-components";

const Input = styled.input`
	padding: 15px 193px 15px 9px;
	font-size: 17px;
	outline: none;
	border: 1px solid #989898;

	@media (max-width: 376px) {
		padding: 15px 140px 15px 9px;
	}

	@media (max-width: 321px) {
		padding: 15px 100px 15px 9px;
	}
`;

const Form = styled.form`
	display: grid;
	grid-gap: 2rem;
	justify-items: center;
`;

const Button = styled.button`
	padding: 5px 120px;
	font-size: 25px;
	background: #0c56cc;
	color: #fff;
	outline: none;
	border: none;
	cursor: pointer;

	@media (max-width: 376px) {
		padding: 5px 96px;
	}

	@media (max-width: 321px) {
		padding: 5px 76px;
	}
`;

const H1 = styled.h1`
	margin-bottom: 5%;
`;

const Div = styled.div`
	display: flex;
	justify-content: center;
	margin-top: 20%;

	@media (max-width: 768px) {
		margin-top: 3%;
		padding-bottom: 50px;
	}

	@media (min-height: 1367px) {
		margin-top: 0;
	}
`;

const Login = () => {
	const { register, handleSubmit, errors } = useForm();
	const onSubmit = (data) => console.log(data);
	return (
		<Div>
			<Form onSubmit={handleSubmit(onSubmit)}>
				<H1>Ingresa tu cuenta</H1>
				<Input
					placeholder="Correo Electrónico"
					name="email"
					ref={register({
						required: { value: true, message: "cagaste correo" },
					})}
				/>
				<label>{errors?.email?.message}</label>
				<Input
					placeholder="Contraseña"
					name="password"
					ref={register({
						required: { value: true, message: "cagaste contraseña" },
					})}
				/>
				<label>{errors?.password?.message}</label>
				{/* <Link href="/Store"> */}
				<Button type="submit">Iniciar sesión</Button>
				{/* </Link> */}
			</Form>
		</Div>
	);
};

export default Login;
